import React from "react";
import {Link} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {detailsUser} from "../actions/UserActions";
import {useEffect} from "react";
import {useState} from "react";



function SigninScreen(props) {

    const [qty, setQty] = useState(1);
    const UserDetails = useSelector(state => state.UserDetails);
    const {User, loading, error} = UserDetails;
    const dispatch = useDispatch();

    useEffect(() =>{



        return () =>{
            //
        };
    },[])


   


    return <div>
        <div>
            <Link to="/">Back to home</Link>
        </div>
        {loading ? <div>Loading...</div>:
            error ? <div>{error}</div>: (<div className="details">
                <div className="details-image">
                    <img src={User.image} alt="Userimg"/>
                </div>
                <div className="details-info">
                    <ul>
                        <li>
                            <h4>{User.name}</h4>
                        </li>
                        <li>
                            {User.brand}
                        </li>
                        <li>
                            Price:<b>${User.price}</b>
                        </li>
                        <li>
                            Description:
                            <div className="details-description">
                                {User.description}
                            </div>
                        </li>
                    </ul>
                </div>
                <div className="details-action">
                    <ul>
                        <li>
                            Price: ${User.price}
                        </li>
                        <li>
                            Status: {
                            User.countInStock > 0 ? "In Stock" :
                                "Out of stock"
                        }
                        </li>
                        <li>
                            Qty: {
                            User.countInStock > 0 && <select value={qty} onChange={(e) => {setQty(e.target.value)} }>
                                {[...Array(User.countInStock).keys()].map(x =>
                                    <option key={x+1} value={x+1}>{x+1}</option>)}
                            </select>}
                            {
                                User.countInStock === 0 && "0"
                            }
                        </li>
                        <li>
                            {
                                User.countInStock > 0 && <button onClick={handleAddtoCart} className="button primary">Add to Cart</button>
                            }
                        </li>
                    </ul>
                </div>
            </div>)}
    </div>
}
export default SigninScreen;
