import logo from './logo.svg';
import './App.css';
import data from './data';
import {BrowserRouter, Router} from "react-router-dom";
import HomeScreen from "./Screens/HomeScreen";
import ProductScreen from "./Screens/ProductScreen";

function App() {

    const openMenu = ()=>{
        document.querySelector(".sidebar").classList.add("open");
    }
    const closeMenu = ()=>{
        document.querySelector(".sidebar").classList.remove("open");
    }

  return (
      <BrowserRouter>
      <div className="grid-container">
          <header className="header">
              <div className="brand">
                  <button onClick={openMenu}>
                      ☰
                  </button>
                  <a href="index.html">amazona</a>
              </div>
              <div className="header-links">
                  <a href="cart.html">Cart</a>
                  <a href="signin.html">Sign in</a>
              </div>
          </header>
          <aside className="sidebar">
              <h3>Shopping Categories</h3>
              <button className="sidebar-close-button" onClick={closeMenu}>x</button>
              <ul>
                  <li>
                      <a href="index.html">Pants</a>
                  </li>
                  <li>
                      <a href="index.html">Shirts</a>
                  </li>
              </ul>
          </aside>
          <main className="main">
              <div className="content">
                  <Router path="/products/:id" component={ProductScreen}></Router>
                  <Router path="/" exact={true} component={HomeScreen}></Router>
                  <ul className="products">
                      {
                          data.products.map(product =>
                              <li>
                                  <div className="product">
                                      <img className="product-image" src={product.image} alt="product1" />
                                      <div className="product-name"><a href="product.html">{product.name}</a></div>
                                      <div className="product-brand">{product.brand}</div>
                                      <div className="product-price">{product.price}</div>
                                  </div>
                              </li>)}
                  </ul>
              </div>
          </main>
          <footer className="footer">
              All rights reserved.
          </footer>
      </div>
      </BrowserRouter>
  );
}

export default App;
